mapper.select:

	select no, title, content, writer, regdate, reply
	from onetoone
	where no = #{no }


	select N2.no, N2.title, N2.content, N2.writer, N2.regdate from 
		( select @ROWNUM:=@ROWNUM+1 num, N1.* 
			from (
					select * 
				from onetoone
				order by regdate desc 
				) as N1
			WHERE (@rownum:=0)=0 and writer= #{name }
		) as N2
	where num between #{start } and #{end }


	select N2.no, N2.title, N2.content, N2.writer, N2.regdate from 
		( select @ROWNUM:=@ROWNUM+1 num, N1.* 
			from (
					select * 
				from onetoone
				order by regdate desc 
				) as N1
			WHERE (@rownum:=0)=0
		) as N2
	where num between #{start } and #{end }


	select count(*)
	from onetoone
	where writer = #{nickname }


	select N2.no, N2.title, N2.content, N2.writer, N2.regdate from 
		( select @ROWNUM:=@ROWNUM+1 num, N1.* 
			from (
					select * 
					from onetoone
					order by regdate desc 
				) as N1
			WHERE (@rownum:=0)=0 and title like concat('%',#{keyword },'%') or content like concat('%',#{keyword },'%')
		) as N2
	where num between #{start } and #{end }


	select N2.no, N2.title, N2.content, N2.writer, N2.regdate from 
		( select @ROWNUM:=@ROWNUM+1 num, N1.* 
			from (
					select * 
				from onetoone
				order by regdate desc 
				) as N1
			WHERE (@rownum:=0)=0 and title like concat('%',#{keyword},'%')
		) as N2
	where num between #{start } and #{end }

mapper.update:

	update onetoone
	set title=#{title }, content=#{content } 
	where no=#{no }


	update onetoone
	set reply=#{reply }
	where no=#{no }
